template.template_content:
{$issueData['SHOW_PAGES']}
<br />
<br style='clear:both;' />
<table cellspacing='0' cellpadding='0' class='ipb_table timeline_no_border'>
	<caption class='maintitle'>
		<span class='right desc'><a href='{parse url="showissue={$issueData['issue_id']}" seotitle="{$issueData['name_seo']}" template="showissue" base="publicWithApp"}' title='{$this->lang->words['bt_timeline_return']}'>{$this->lang->words['bt_timeline_return']}</a></span>
		{$this->lang->words['bt_timeline_viewing']} {$issueData['title']}	
	</caption>
</table>
	{parse striping="timeline" classes="row1,row2"}

	<foreach loop="$data as $k => $v">
		<if test="isset( $v['timelineNewDay'] ) && $v['timelineNewDay'] == true">
			<if test="isset( $v['timelineEndDay'] ) && $v['timelineEndDay'] == true">
				</table>
				</div>
			</if>

			<h3 class='timeline_day bar'>
				<span class='timelineCollapse desc right' id='collapse-{$v['timelineStamp']}'>{$this->lang->words['bt_timeline_collapse']}</span>
				{$v['timelineDay']} <span class='desc'>({$daysData[ $v['timelineDay'] ]} {$this->lang->words['bt_timeline_entries']})</span>
			</h3>

		<div class='collapsedTable' id='day-{$v['timelineStamp']}'>
		<table cellspacing='0' cellpadding='0' class='ipb_table timeline_no_border'>
		</if>

		<tr class='{parse striping="timeline"}'>
			<td class='timeline_icon altrow'>
				<if test="$v['module']=='severity'">
					<img src='{$this->settings['img_url']}/tracker/severity.png' alt='{$this->lang->words['bt_timeline_change_sev']}' />
				</if>

				<if test="$v['module']=='version'">
					<img src='{$this->settings['img_url']}/tracker/version.png' alt='{$this->lang->words['bt_timeline_change_ver']}' />
				</if>

				<if test="$v['module']=='post'">
					<img src='{$this->settings['img_url']}/tracker/reply.png' alt='{$this->lang->words['bt_timeline_change_reply']}' />
				</if>

				<if test="$v['module']=='attach'">
					<img src='{$this->settings['img_url']}/tracker/attach.png' alt='{$this->lang->words['bt_timeline_change_att']}' />
				</if>

				<if test="$v['module']=='issue_started'">
					<img src='{$this->settings['img_url']}/tracker/issue_submitted.png' alt='{$this->lang->words['bt_timeline_issue_started']}' />
				</if>

				<if test="$v['module']=='status'">
					<img src='{$this->settings['img_url']}/tracker/status.png' alt='{$this->lang->words['bt_timeline_change_stat']}' />
				</if>

				<if test="$v['module']=='issue_lock' && $v['new_value']=='closed'">
					<img src='{$this->settings['img_url']}/tracker/issue_locked.png' alt='{$this->lang->words['bt_timeline_change_clos']}' />
				</if>

				<if test="$v['module']=='issue_lock' && $v['new_value']=='open'">
					<img src='{$this->settings['img_url']}/tracker/issue_unlocked.png' alt='{$this->lang->words['bt_timeline_change_reop']}' />
				</if>

				<if test="$v['module']=='custom'">
					<img src='{$this->settings['img_url']}/tracker/custom.png' alt='{$this->lang->words['bt_timeline_change_cf']}' />
				</if>
			</td>

			<td class='timeline_entry altrow'>
				<php>
					$v['field_type_display']		= $v['module'];
					$v['new_value_display']	= $v['new_value'];
					$v['changed']					= $this->lang->words['bt_timeline_changed'];
					$v['to']						= $this->lang->words['bt_timeline_to'];

					if ( $v['module'] == 'severity' )
					{
						$v['new_value_display'] = "<span class='severity-{$v['new_value']}'>" . $v['new_value'] . ' - ' . $this->lang->words['bt_global_severity_dd_' .  $v['new_value'] ] . '</span>';
					}

					if ( $v['module'] == 'version' && $v['field_extra'] == 'fixed' )
					{
						$v['field_type_display']	= $this->lang->words['bt_timeline_fixed_in_ver'];
					}

					if ( $v['module'] == 'post' )
					{
						$v['field_type_display']		= '';
						$v['changed']					= $this->lang->words['bt_timeline_added_reply'];
						$v['new_value_display']	= '';
						$v['to']						= '';
					}

					if ( $v['module'] == 'attach' )
					{
						$v['field_type_display']		= '';
						$v['changed']					= $this->lang->words['bt_timeline_added_att'];
						$v['new_value_display']	= '';
						$v['to'] 						= '';
					}

					if ( $v['module'] == 'issue_started' )
					{
						$v['field_type_display']		= '';
						$v['changed']					= $this->lang->words['bt_timeline_created_issue'];
						$v['new_value_display']	= '';
						$v['to'] 						= '';
					}

					if ( $v['module'] == 'issue_lock' )
					{
						$v['field_type_display']		= '';
						$v['new_value_display']	= '';
						$v['to']						= $this->lang->words['bt_timeline_issue'];

						if ( $v['new_value'] == 'closed' )
						{
							$v['changed'] = 'closed';
						}
						else
						{
							$v['changed'] = 'reopened';
						}
					}

					if ( $v['module'] == 'status' )
					{
						$v['new_value_display']	= $this->registry->tracker->status[ $v['new_value'] ]['cat_title'];
					}
				</php>
				<a href='{parse url="showuser={$v['member_id']}" seotitle="{$v['members_seo_name']}" template="showuser" base="public"}'>{$v['members_display_name']}</a>{parse template="user_popup" group="global" params="$v['member_id'],$v['members_seo_name']"} {$v['changed']} {$v['field_type_display']} {$v['to']} <strong>{$v['new_value_display']}</strong>
			</td>

			<td class='timeline_date altrow desc'>
				{$v['timelineEntryDate']}
			</td>
		</tr>
	</foreach>
</table>
</div>
